CREATE TABLE lock_candidat (num_dossier_opi_cpt_min varchar(8) NOT NULL comment 'le numero de dossier du compte a minima locké', ressource_lock varchar(40) NOT NULL comment 'la ressource du compte a minima lockée', ui_id_lock int(10) NOT NULL comment 'l''id de l''ui ayant locké la ressource', instance_id_lock varchar(20) NOT NULL comment 'l''instance de l''application', PRIMARY KEY (num_dossier_opi_cpt_min, ressource_lock)) comment='table des locks eCandidat' ENGINE=InnoDB;