CREATE TABLE load_balancing_reload_run (dat_last_check_lb_reload_run timestamp NOT NULL comment 'deniere verification de l''instance', instance_id_lb_reload_run varchar(20) NOT NULL comment 'id de l''instance', PRIMARY KEY (dat_last_check_lb_reload_run, instance_id_lb_reload_run)) comment='table de besoin de reload de liste en cache' ENGINE=InnoDB;
CREATE TABLE load_balancing_reload (cod_data_lb_reload varchar(20) NOT NULL comment 'code de la donnée a recharger', dat_cre_lb_reload timestamp NOT NULL comment 'date de création du chargement', PRIMARY KEY (cod_data_lb_reload)) comment='table des éléments à recharger' ENGINE=InnoDB;