DROP TABLE `siscol_utilisateur`;
CREATE TABLE siscol_utilisateur (id_uti int(10) NOT NULL comment 'id de l''utilisateur', cod_uti varchar(30) NOT NULL comment 'Code Utilisateur D"APOGEE connu d"ORACLE', cod_cge varchar(3) comment '(COPIED)Code Centre de Gestion d"appartenance de l"utilisateur', lib_cmt_uti varchar(200) comment 'Libelle long associe a l"utilisateur', tem_en_sve_uti bit(1) NOT NULL comment 'Temoin code en service de l"utilisateur', adr_mail_uti varchar(200) comment 'Adresse mail de l''utilisateur', CONSTRAINT UTI_PK PRIMARY KEY (id_uti), CONSTRAINT DMD978A CHECK ( TEM_AUT_SPE_MCC IN ( 'O','N' ) )) comment='Rérérentiel SiScol : Table des utilisateurs' ENGINE=InnoDB;